'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';

// Define the structure of the data (this should ideally be in a types file)
interface WhitepaperData {
title: string;
sections: Array\<{
id: string;
title: string;
content: string;
}\>;
}

const InteractiveWhitepaper = () =\> {
const [data, setData] = useState\<WhitepaperData | null\>(null);
const [currentSectionIndex, setCurrentSectionIndex] = useState(0);
const [loading, setLoading] = useState(true);

useEffect(() =\> {
const loadData = async () =\> {
try {
// We assume the data file will be available in the public directory after build
// In a real Next.js app, you might import it directly if it's in src/data
// For this scaffolding, we'll simulate loading from public (requires moving the file post-generation or adjusting paths)
// Let's adjust the approach to import directly from src/data for Next.js compatibility during dev.

```
    // Dynamic import simulation for scaffolding purposes
    const jsonData = await import('@/data/whitepaperContent.json');
    setData(jsonData.default);

  } catch (error) {
    console.error("Failed to load whitepaper content:", error);
  } finally {
    setLoading(false);
  }
};
loadData();
```

}, []);

if (loading) {
return \<div className="p-8 text-center"\>Loading interactive content...\</div\>;
}

if (\!data || \!data.sections || data.sections.length === 0) {
return \<div className="p-8 text-center text-destructive"\>Failed to load content or content is empty.\</div\>;
}

const currentSection = data.sections[currentSectionIndex];

const handleNext = () =\> {
if (currentSectionIndex \< data.sections.length - 1) {
setCurrentSectionIndex(currentSectionIndex + 1);
}
};

const handlePrev = () =\> {
if (currentSectionIndex \> 0) {
setCurrentSectionIndex(currentSectionIndex - 1);
}
};

return (
\<div className="border rounded-lg shadow-lg p-6 bg-card"\>
\<h2 className="text-2xl font-semibold mb-4"\>{data.title}\</h2\>

```
  <div className="mb-6 p-4 border rounded-md bg-background">
    <h3 className="text-xl font-bold mb-2">{currentSection.title}</h3>
    <p>{currentSection.content}</p>
  </div>

  <div className="flex justify-between items-center">
    <Button
      onClick={handlePrev}
      disabled={currentSectionIndex === 0}
    >
      Previous
    </Button>
    <span className="text-sm text-muted-foreground">
      Section {currentSectionIndex + 1} of {data.sections.length}
    </span>
    <Button
      onClick={handleNext}
      disabled={currentSectionIndex === data.sections.length - 1}
    >
      Next
    </Button>
  </div>
</div>
```

);
};

