// src/components/showcase/InteractiveWhitepaper.tsx
'use client';

import React, { useState } from 'react';
// We import the JSON directly as we placed it in the src/data directory
import reportData from '@/data/whitepaperContent.json';
import { cn } from '@/lib/utils';

// Define the types based on the proposed JSON structure in A174
interface Page {
pageId: string;
pageTitle: string;
tldr: string;
content: string;
// Image prompts are ignored for this initial placeholder
}

const InteractiveWhitepaper = () =\> {
// Flatten the structure for easier navigation
const allPages: Page[] = reportData.sections.flatMap(section =\> section.pages);
const [currentPageIndex, setCurrentPageIndex] = useState(0);

const currentPage = allPages[currentPageIndex];

const handleNext = () =\> {
if (currentPageIndex \< allPages.length - 1) {
setCurrentPageIndex(currentPageIndex + 1);
}
};

const handlePrev = () =\> {
if (currentPageIndex \> 0) {
setCurrentPageIndex(currentPageIndex - 1);
}
};

if (\!currentPage) {
return \<div className="p-6"\>No content found in the report data.\</div\>;
}

// Basic button styling using Tailwind classes (mimicking a 'primary' button)
const buttonClasses = cn(
"px-4 py-2 bg-primary text-primary-foreground rounded-md shadow-sm hover:bg-primary/90 disabled:opacity-50 transition-colors"
);

return (
\<div className="border rounded-lg shadow-lg p-8 bg-card max-w-4xl mx-auto"\>
\<h2 className="text-3xl font-semibold mb-6 text-center"\>{reportData.reportTitle}\</h2\>

```
  <div className="mb-8">
    <h3 className="text-2xl font-bold mb-3">{currentPage.pageTitle}</h3>

    <div className="mb-4 p-4 bg-muted rounded-md">
        <p className="italic text-muted-foreground">{currentPage.tldr}</p>
    </div>

    <div className="prose dark:prose-invert max-w-none">
      {/* Content display logic */}
      <p>{currentPage.content}</p>
    </div>
  </div>

  <div className="mt-8 pt-4 border-t flex justify-between items-center">
    <button
      onClick={handlePrev}
      disabled={currentPageIndex === 0}
      className={buttonClasses}
    >
      Previous
    </button>
    <span className="text-sm text-muted-foreground">
      Page {currentPageIndex + 1} of {allPages.length}
    </span>
    <button
      onClick={handleNext}
      disabled={currentPageIndex === allPages.length - 1}
      className={buttonClasses}
    >
      Next
    </button>
  </div>
</div>
```

);
};

export default InteractiveWhitepaper;
\</file\_artifact\>

